        :timestamp, # timestamp
        :type, # shortstr
        :user_id, # shortstr
        :app_id, # shortstr
        :cluster_id, # shortstr

      ]

      def self.encode_content_type(value)
        f = "0x%04x" % [1 << 15, 15] # (1 << 0)
        result = [].pack("!B")
        [0, f, result]
      end

      def self.encode_content_encoding(value)
        f = "0x%04x" % [1 << 14, 14] # (1 << 1)
        result = [].pack("!B")
        [1, f, result]
      end

      def self.encode_headers(value)
        f = "0x%04x" % [1 << 13, 13] # (1 << 2)
        result = [].pack("!")
        [2, f, result]
